// Parsear output del Agente 1 (formato: campo1|||campo2|||...)
const output = $input.first().json.output || $input.first().json.text;
const parts = output.split('|||').map(p => p.trim());

if (parts.length !== 7) {
  throw new Error(`Error: Agente 1 devolvi√≥ ${parts.length} campos, se esperaban 7`);
}

return {
  // Datos del lead
  nombre_cliente: parts[0],
  telefono_cliente: parts[1],
  email_cliente: parts[2],
  dolencia_principal: parts[3],
  servicio_interes: parts[4],
  urgencia: parts[5],
  intencion: parts[6],

  // Metadatos
  source: 'Instagram CTWA',
  timestamp: new Date().toISOString(),
  whatsapp_phone: $('üì± Webhook WhatsApp').item.json.body?.messages?.[0]?.from || 'unknown',
  message_original: $('üì± Webhook WhatsApp').item.json.body?.messages?.[0]?.text?.body || '',

  // Generar lead_id √∫nico
  lead_id: `${Date.now()}-${parts[1].replace(/\+/g, '')}`
};

