// Filtrar leads que están esperando respuesta hace más de 72h
const items = $input.all();
const ahora = new Date();
const TIMEOUT_HORAS = 72;

const leadsSilenciosos = items.filter(item => {
  const estado = item.json.Estado;
  const ultimoContacto = new Date(item.json.Ultimo_Contacto);
  const horasPasadas = (ahora - ultimoContacto) / (1000 * 60 * 60);

  // Solo procesar estados activos que esperan respuesta
  const estadosActivos = ['M1_Enviado', 'M2_Enviado', 'M3_Enviado', 'Caliente_Oferta_Enviada'];

  return estadosActivos.includes(estado) && horasPasadas > TIMEOUT_HORAS;
});

return leadsSilenciosos;

// Decidir acción según contador de mensajes y estado
const lead = $input.item.json;
const contador = parseInt(lead.Contador_Mensajes) || 0;
const estado = lead.Estado;

let accion = '';
let nuevoEstado = '';
let mensaje = '';

if (contador < 3) {
  // Aún puede avanzar al siguiente mensaje
  if (estado === 'M1_Enviado') {
    accion = 'enviar_m2_recordatorio';
    nuevoEstado = 'M2_Enviado';
    mensaje = `Hola ${lead.Nombre}, ¿pudiste ver mi mensaje anterior?\n\nPara ayudarte mejor con tu ${lead.Tipo_Dolor || 'dolor'}, ¿me cuentas si te impide hacer tus actividades normales?`;
  } else if (estado === 'M2_Enviado') {
    accion = 'enviar_m3_suave';
    nuevoEstado = 'M3_Enviado';
    mensaje = `Hola ${lead.Nombre}, entiendo que puedes estar ocupado/a.\n\nTe dejo esta guía gratuita que puede ayudarte: [LINK_PDF]\n\n¿Te gustaría una valoración profesional?`;
  } else if (estado === 'M3_Enviado' || estado === 'Caliente_Oferta_Enviada') {
    accion = 'pasar_frio';
    nuevoEstado = 'Frio';
    mensaje = '';
  }
} else {
  // Ya envió 3+ mensajes sin respuesta → Frío
  accion = 'pasar_frio';
  nuevoEstado = 'Frio';
  mensaje = '';
}

return {
  json: {
    ...lead,
    accion: accion,
    nuevo_estado: nuevoEstado,
    mensaje_enviar: mensaje,
    contador_actualizado: accion.includes('enviar') ? contador + 1 : contador
  }
};
