// Obtener el contenido HTML o JSON de entrada
const content = $json.body || $json.data || $json;

// Convertir todo a texto, en caso sea JSON o HTML
const text = typeof content === 'string' ? content : JSON.stringify(content);

// Expresi√≥n regular para encontrar enlaces loom
const regex = /https?:\/\/(www\.)?loom\.com\/share\/[a-zA-Z0-9]+/g;

// Buscar todas las coincidencias
const matches = text.match(regex) || [];

// Quitar duplicados usando un Set
const uniqueLinks = Array.from(new Set(matches));

// Formatear para la ejecuci√≥n con yt-dlp
return uniqueLinks.map(link => ({ json: { command: `./yt-dlp "${link}"` } }));

