const data = items[0].json;

// Funci√≥n recursiva para recorrer el JSON
function extractLinks(obj) {
  let results = [];
  if (typeof obj === "string") {
    if (obj.includes("loom") || obj.endsWith(".gif")) {
      results.push(obj);
    }
  } else if (Array.isArray(obj)) {
    obj.forEach(item => {
      results = results.concat(extractLinks(item));
    });
  } else if (typeof obj === "object" && obj !== null) {
    Object.values(obj).forEach(val => {
      results = results.concat(extractLinks(val));
    });
  }
  return results;
}

// Extraer los links
const links = extractLinks(data);

// Eliminar duplicados
const uniqueLinks = [...new Set(links)];

// Devolver como items en n8n
return uniqueLinks.map(link => ({ json: { url: link } }));
