// INPUT: $json.output.message_html (HTML del agente)
const RAW = ($input.first().json.output && $input.first().json.output.message_html) || '';

const BRAND = '#FF9433';
const FONT = "Segoe UI, Tahoma, Geneva, Verdana, sans-serif";

// INFORMACI√ìN REQUERIDA AQUI!!!
const CTA = 'https://cal.com/aima-xwyn8i/llamada';
const NAME = 'Abde Idrissi';

// Estilos CTA con degradado (inline-safe)
const CTA_INLINE =
  "display:inline-block;background:linear-gradient(90deg,rgba(255,148,51,1) 30%, rgba(193,175,195,1) 96%);" +
  "color:#fff;text-decoration:none;padding:18px 40px;border-radius:16px;font-weight:700;font-size:16px;" +
  "box-shadow:0 12px 35px rgba(255,148,51,0.4);text-align:center;min-width:240px;letter-spacing:.5px;" +
  "text-transform:uppercase;";

// Evita reprocesar si ya est√° marcado
if (/<!--AUTOOPS-CARDS-->/i.test(RAW)) {
  return [{ email_html: RAW }];
}

// 1) Limpia estilos en <a> y aplica color marca
let src = RAW.replace(/<a\b([^>]*?)style="[^"]*?"([^>]*)>/gi, '<a $1$2>');
src = src.replace(/<a\b([^>]*)>/gi,
  `<a $1 style="color:${BRAND};text-decoration:underline;text-underline-offset:2px;">`
);

// 2) Convierte <ol><li> en cards numeradas con tablas + inline
let idx = 1;
function liToCard(n, innerHtml){
  const m = innerHtml.match(/<p[^>]*>\s*<strong>([\s\S]*?)<\/strong>\s*<\/p>/i);
  const title = (m ? m[1] : `Mejora ${n}`).trim();
  const body = (m ? innerHtml.replace(m[0], '') : innerHtml).trim();

  return `
  <!-- card -->
  <table role="presentation" width="100%" cellpadding="0" cellspacing="0" style="border-collapse:separate;margin:0 0 14px 0;">
    <tr>
      <td style="background:#FFF3E9;border:1px solid #FFDBC3;border-radius:16px;padding:16px;">
        <table role="presentation" width="100%" cellpadding="0" cellspacing="0">
          <tr>
            <td valign="top" width="42" style="width:42px;">
              <div style="width:34px;height:34px;border-radius:12px;background:${BRAND};color:#fff;
                          font-weight:800;font-size:14px;line-height:34px;text-align:center;
                          font-family:${FONT};box-shadow:0 6px 14px -8px rgba(255,148,51,.6);">
                ${n}
              </div>
            </td>
            <td style="padding-left:8px;">
              <div style="font-weight:800;font-size:16px;color:#111;margin:0 0 6px 0;font-family:${FONT};">
                ${title}
              </div>
              <div style="font-size:15px;color:#222;line-height:1.65;font-family:${FONT};">
                ${body}
              </div>
            </td>
          </tr>
        </table>
      </td>
    </tr>
  </table>`;
}

src = src.replace(/<ol[\s\S]*?<\/ol>/gi, (ol) => {
  const items = [...ol.matchAll(/<li[^>]*>([\s\S]*?)<\/li>/gi)].map(m => m[1]);
  if (!items.length) return ol;
  const cards = items.map(li => liToCard(idx++, li)).join('');
  return cards;
});

// 3) HTML completo, email-safe (tablas + inline) con CTA degradado y footer oscuro
const html = `
<!DOCTYPE html>
<html lang="es"><head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width">
<title>Diagn√≥stico ‚Äì AutoOps</title>
</head>
<body style="margin:0;background:#f5f7fb;color:#2c3e50;font-family:${FONT};line-height:1.6;">
  <!--AUTOOPS-CARDS-->
  <table role="presentation" cellpadding="0" cellspacing="0" border="0" width="100%">
    <tr>
      <td align="center" style="padding:24px 12px;">
        <table role="presentation" cellpadding="0" cellspacing="0" width="600" style="max-width:600px;background:#ffffff;border-radius:16px;border:1px solid #e5e7eb;box-shadow:0 10px 30px rgba(0,0,0,0.08);">
          <!-- contenido -->
          <tr>
            <td style="padding:22px;">
              <div style="font-size:16px;line-height:1.7;color:#374151;font-family:${FONT};">
                ${src}
              </div>
            </td>
          </tr>

          <!-- CTA inferior -->
          <tr>
            <td align="center" style="padding:0 22px 22px 22px;">
              <a href="${CTA}" target="_blank" style="${CTA_INLINE}">Programar una llamada</a>
            </td>
          </tr>
        </table>

        <!-- footer oscuro -->
        <table role="presentation" cellpadding="0" cellspacing="0" width="auto" style="margin-top:16px;background:#1a202c;border-radius:16px;">
          <tr>
            <td align="center" style="padding:32px 24px;">
              <div style="font-family:${FONT};font-size:24px;font-weight:700;color:${BRAND};margin-bottom:8px;">${NAME}</div>
              <div style="font-family:${FONT};font-size:16px;color:#cbd5e0;margin-bottom:24px;">Recupera el tiempo que tu negocio te quita</div>

              <!-- social -->
              <table role="presentation" cellpadding="0" cellspacing="0" align="center" style="margin-bottom:24px;">
                <tr>
                  <td align="center" style="padding:0 8px;">
                    <!-- INFORMACI√ìN REQUERIDA -->
                    <a href="mailto:abdeidrissi.ia@gmail.com"
                       style="display:inline-block;width:44px;height:44px;line-height:44px;text-align:center;border-radius:50%;
                              background:rgba(255,148,51,.10);text-decoration:none;">
                      <img src="https://img.icons8.com/color/ffffff/gmail.png" width="22" height="22" alt="Email"
                           style="display:inline-block;border:0;outline:none;text-decoration:none;vertical-align:middle;">
                    </a>
                  </td>
                  <td align="center" style="padding:0 8px;">
                    <!-- INFORMACI√ìN REQUERIDA -->
                    <a href="https://aimaspain.com/" target="_blank"
                       style="display:inline-block;width:44px;height:44px;line-height:44px;text-align:center;border-radius:50%;
                              background:rgba(255,148,51,.10);text-decoration:none;">
                      <img src="https://img.icons8.com/color/ffffff/instagram-new.png
" width="22" height="22" alt="Website"
                           style="display:inline-block;border:0;outline:none;text-decoration:none;vertical-align:middle;">
                    </a>
                  </td>
                </tr>
              </table>
              <div style="font-family:${FONT};font-size:14px;color:#94a3b8;line-height:1.5;">
                Este an√°lisis ha sido preparado espec√≠ficamente para tu negocio.<br>
                Si tienes preguntas, no dudes en contactarme directamente.
              </div>
            </td>
          </tr>
        </table>

      </td>
    </tr>
  </table>
</body></html>
`.replace(/\n\s+\n/g, '\n');

return [{ 
  email_html: html,
  correo:  $('Mapeo correo - web').first().json.correo// o de donde venga el email
}];

const html = $input.first().json.data || '';
const cleaned = html
  .replace(/<script[\s\S]*?<\/script>/gi,' ')
  .replace(/<style[\s\S]*?<\/style>/gi,' ')
  .replace(/<!--[\s\S]*?-->/g,' ')
  .replace(/<[^>]+>/g,' ')
  .replace(/\s+/g,' ')
  .trim();

const text = cleaned.slice(0, 28000); // duro pero seguro (‚âà6-8k tokens)
const signals = {
  url: $json.url || '',
  page_title: $json.pageTitle || '',
  has_wa: /wa\.me|api\.whatsapp\.com/i.test(html),
  has_tel: /tel:/i.test(html),
  has_mailto: /mailto:/i.test(html),
  has_checkout: /checkout|carrito|add to cart|woocommerce/i.test(html),
  has_booking: /calendly|book now|appointment|reserva/i.test(html),
  social: [...new Set((html.match(/instagram\.com|linkedin\.com|facebook\.com|youtube\.com/gi) || []).map(s => s.toLowerCase()))]
};

return [{ url: signals.url, pageTitle: signals.page_title, text, signals }];
