// Funci√≥n para quitar tildes y limpiar texto
const normalize = (str) =>
  str.normalize("NFD").replace(/[\u0300-\u036f]/g, "").toLowerCase().trim();

const originalText = $json.text || '';
const text = normalize(originalText);

// Nuevos patrones: buscame, quiero, necesito, hay... [nicho] en [ciudad]
const regex = /\b(?:busc(a|ame|qu?ame)|quiero|necesito|hay)\s+(.*?)\s+en\s+(.+?)([.,\s]|$)/i;
const match = text.match(regex);

if (match && match[2] && match[3]) {
  const niche = match[2].trim();
  const city = match[3].trim();
  return [
    {
      json: {
        niche,
        city,
        original: originalText
      }
    }
  ];
} else {
  return [
    {
      json: {
        error: "‚ùå No se pudo extraer el nicho y la ciudad.",
        original: originalText
      }
    }
  ];
}

const inputData = $input.first().json;

// Los datos est√°n directamente en el nivel ra√≠z, no en .data
if (!inputData.places || inputData.places.length === 0) {
  return [];
}

return inputData.places.map((place, index) => ({
  json: {
    name: place.displayName?.text || '',
    website: place.websiteUri || '',
    address: place.formattedAddress || '',
    phone: place.nationalPhoneNumber || '',
    rating: place.rating || 0,
    reviewCount: place.userRatingCount || 0,
    businessStatus: place.businessStatus || '',
    googleMapsUrl: place.googleMapsUri || ''
  }
}));

// Obtener contenido de m√∫ltiples fuentes posibles
let input = '';
if ($input.first().json.data) {
  input = $input.first().json.data.toString();
} else if ($input.first().json.body) {
  input = $input.first().json.body.toString();
} else if ($input.first().json) {
  input = JSON.stringify($input.first().json);
}

if (!input) {
  return [{ json: { email: '' } }];
}

// Limpiar y decodificar contenido
input = input
  .replace(/\\n/g, ' ')
  .replace(/\\t/g, ' ')
  .replace(/\\"/g, '"')
  .replace(/&quot;/g, '"')
  .replace(/&amp;/g, '&')
  .replace(/&lt;/g, '<')
  .replace(/&gt;/g, '>')
  .replace(/&#64;/g, '@')
  .replace(/\[at\]/gi, '@')
  .replace(/\(at\)/gi, '@')
  .replace(/\s*at\s*/gi, '@')
  .replace(/\[dot\]/gi, '.')
  .replace(/\(dot\)/gi, '.')
  .replace(/\s*dot\s*/gi, '.');

// Array de patrones de regex m√°s avanzados
const patterns = [
  // Patr√≥n est√°ndar mejorado
  /\b[A-Za-z0-9]([A-Za-z0-9._%-]*[A-Za-z0-9])?@[A-Za-z0-9]([A-Za-z0-9.-]*[A-Za-z0-9])?\.[A-Za-z]{2,}\b/gi,

  // Emails con espacios (anti-spam)
  /\b[A-Za-z0-9._%-]+\s*@\s*[A-Za-z0-9.-]+\s*\.\s*[A-Za-z]{2,}\b/gi,

  // Emails en JavaScript (mailto:)
  /mailto\s*:\s*([A-Za-z0-9._%-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,})/gi,

  // Emails en atributos HTML
  /(?:email|mail|contact)["'\s]*[:=]["'\s]*([A-Za-z0-9._%-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,})/gi,

  // Emails ofuscados con [at] y [dot]
  /\b[A-Za-z0-9._%-]+\s*\[at\]\s*[A-Za-z0-9.-]+\s*\[dot\]\s*[A-Za-z]{2,}\b/gi,

  // Emails en JSON
  /"[^"]*@[^"]*\.[A-Za-z]{2,}"/gi
];

let allEmails = new Set();

// Buscar con cada patr√≥n
patterns.forEach(pattern => {
  const matches = input.match(pattern);
  if (matches) {
    matches.forEach(match => {
      // Limpiar el match
      let email = match
        .replace(/^mailto\s*:\s*/i, '')
        .replace(/^[^:]*[:=]["'\s]*/i, '')
        .replace(/["']/g, '')
        .replace(/\s+/g, '')
        .toLowerCase()
        .trim();

      // Validar que sea un email v√°lido
      if (email.includes('@') && email.includes('.') && email.length > 5) {
        allEmails.add(email);
      }
    });
  }
});

// Filtros adicionales para emails espec√≠ficos en texto plano
const textPatterns = [
  // Buscar patrones como "contacto arroba empresa punto com"
  /(?:contacto|info|ventas|hola|email)\s*(?:arroba|@)\s*[A-Za-z0-9.-]+\s*(?:punto|\.)\s*(?:com|es|org|net)/gi,

  // Emails separados por espacios
  /\b[A-Za-z0-9._%-]+\s+arroba\s+[A-Za-z0-9.-]+\s+punto\s+[A-Za-z]{2,}\b/gi
];

textPatterns.forEach(pattern => {
  const matches = input.match(pattern);
  if (matches) {
    matches.forEach(match => {
      let email = match
        .replace(/\s*arroba\s*/gi, '@')
        .replace(/\s*punto\s*/gi, '.')
        .replace(/\s+/g, '')
        .toLowerCase()
        .trim();

      if (email.includes('@') && email.includes('.')) {
        allEmails.add(email);
      }
    });
  }
});

// Convertir Set a Array y filtrar emails no v√°lidos
const validEmails = Array.from(allEmails).filter(email => {
  const cleanEmail = email.toLowerCase();

  // Filtrar emails no deseados
  const invalidTerms = [
    'noreply', 'no-reply', 'donotreply', 'do-not-reply',
    'mailer-daemon', 'postmaster', 'abuse', 'spam',
    'example.com', 'test.com', 'localhost',
    'yourname', 'youremail', 'your-email',
    'contact@example', 'info@example',
    'dummy', 'fake', 'invalid'
  ];

  const hasInvalidTerm = invalidTerms.some(term => cleanEmail.includes(term));
  if (hasInvalidTerm) return false;

  // Validar estructura b√°sica
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(cleanEmail)) return false;

  // Filtrar emails demasiado cortos o largos
  if (cleanEmail.length < 5 || cleanEmail.length > 100) return false;

  return true;
});

// Si no se encontraron emails, devolver un item vac√≠o
if (validEmails.length === 0) {
  return [{ json: { email: '' } }];
}

// Devolver cada email como un item separado
return validEmails.map(email => ({ json: { email } }));

const input = ($input.first().json.data || '').toString();
const regex = /\b[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}\b/gi;
const emails = input.match(regex) ?? [''];
return emails.map(email => ({ json: { email }}))

const emailRegex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g;
const html = $input.all()[0].json.data || '';

// Extraer emails del HTML de la p√°gina principal
const emails = html.match(emailRegex) || [];

// Eliminar duplicados y filtrar emails v√°lidos
const validEmails = [...new Set(emails)].filter(email => {
  return email.includes('@') && 
         email.includes('.') && 
         !email.includes('example.') &&
         !email.includes('test@') &&
         !email.includes('noreply') &&
         email.length > 5;
});

// Obtener la URL base
let baseUrl = $input.all()[0].json.base_url;
if (!baseUrl) {
  baseUrl = $('Loop Over Items').first().json.website; // Cambia por tu URL
}

return [{
  website: baseUrl,
  emails_found: validEmails,
  total_count: validEmails.length,
  scan_date: new Date().toISOString(),
  source: 'main_page_only'
}];
