parameters.jsCode:
// Obtener la salida del agente
const output = $input.item.json.output || $input.item.json.text;

// Separar por comas (considera comas dentro de nombres)
const parts = output.split(',').map(p => p.trim());

return {
  nombre_cliente: parts[0] || 'No mencionado',
  telefono_cliente: parts[1] || 'No mencionado',
  email_cliente: parts[2] || 'No mencionado',
  fecha_preferida: parts[3] || 'No mencionado',
  hora_preferida: parts[4] || 'No mencionado',
  action: parts[5] || 'No mencionado'
};
