// Procesar datos de tendencias de IA
const trendsData = $input.all()[0].json;

let trendingTopics = [];

// Procesar interest_over_time si existe
if (trendsData.interest_over_time) {
  const timelineData = trendsData.interest_over_time.timeline_data;
  if (timelineData && timelineData.length > 0) {
    // Obtener los datos más recientes
    const latestData = timelineData[timelineData.length - 1];

    // Extraer valores de búsqueda para cada término
    latestData.values.forEach((value, index) => {
      const query = trendsData.interest_over_time.timeline_data[0].values[index].query;
      trendingTopics.push({
        topic: query,
        interest: value.value,
        trend: value.extracted_value || 'stable'
      });
    });
  }
}

// Si no hay datos específicos, usar temas predefinidos de IA
if (trendingTopics.length === 0) {
  trendingTopics = [
    { topic: "ChatGPT y modelos de lenguaje", interest: 95, trend: "rising" },
    { topic: "Inteligencia Artificial Generativa", interest: 87, trend: "rising" },
    { topic: "Automatización con IA", interest: 76, trend: "stable" },
    { topic: "Herramientas de IA para creadores", interest: 82, trend: "rising" },
    { topic: "Ética en Inteligencia Artificial", interest: 64, trend: "stable" }
  ];
}

// Ordenar por interés
trendingTopics.sort((a, b) => b.interest - a.interest);

// Seleccionar el tema principal para el guión
const mainTopic = trendingTopics[0];
const secondaryTopics = trendingTopics.slice(1, 4);

return [{
  json: {
    mainTopic: mainTopic,
    secondaryTopics: secondaryTopics,
    allTopics: trendingTopics,
    timestamp: new Date().toISOString()
  }
}];
