const html = $('Loop Over Items').first().json.data || '';

// Limpieza del texto visible
const clean = html
  .replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, '')
  .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, '')
  .replace(/<\/?[^>]+(>|$)/g, '') // eliminar etiquetas HTML
  .slice(0, 3000); // limitar longitud

// Extracci√≥n de colores
const colorRegex = /(#(?:[0-9a-f]{3}){1,2}|rgb\([^)]+\)|rgba\([^)]+\))/gi;
const colores = html.match(colorRegex) || [];

return [{
  json: {
    cleanText: clean,
    colores: colores
  }
}];
