// Procesar y estructurar el contenido generado por IA
const aiResponse = $input.first().json.choices[0].message.content;
const contextData = $('Configurar Contexto Temporal').first().json;

// Funci√≥n para parsear el contenido y crear estructura organizada
function parseAIContentToStructured(content, context) {
  const posts = [];
  let currentPost = {};
  let postCounter = 1;

  // Dividir el contenido en secciones por gui√≥n
  const sections = content.split('üè† PLATAFORMA:').filter(section => section.trim() !== '');

  sections.forEach((section, index) => {
    try {
      // Extraer informaci√≥n de cada secci√≥n
      const lines = section.split('\n').filter(line => line.trim() !== '');

      let post = {
        id: `post_${postCounter}_semana_${context.semana_numero}`,
        weekNumber: context.semana_numero,
        createdDate: context.fecha_actual,
        month: context.mes_actual,
        year: context.a√±o_actual
      };

      // Extraer datos espec√≠ficos usando regex y parsing
      lines.forEach(line => {
        const trimmedLine = line.trim();

        if (trimmedLine.includes('PLATAFORMA:') || (index === 0 && !post.platform)) {
          const platformMatch = trimmedLine.match(/\[(.*?)\]/);
          post.platform = platformMatch ? platformMatch[1].split('/')[0] : 'Instagram';
        }

        if (trimmedLine.includes('üìÖ D√çA:')) {
          const dayMatch = trimmedLine.match(/üìÖ D√çA:\s*\[(.*?)\]/);
          post.publishDay = dayMatch ? dayMatch[1] : 'Lunes';
        }

        if (trimmedLine.includes('üéØ T√çTULO HOOK:')) {
          const titleMatch = trimmedLine.match(/üéØ T√çTULO HOOK:\s*\[(.*?)\]/);
          post.title = titleMatch ? titleMatch[1] : 'Contenido Inmobiliario';
        }

        if (trimmedLine.includes('üî• CTA ESPEC√çFICO:')) {
          const ctaMatch = trimmedLine.match(/üî• CTA ESPEC√çFICO:\s*\[(.*?)\]/);
          post.callToAction = ctaMatch ? ctaMatch[1] : 'Cont√°ctanos para m√°s informaci√≥n';
        }

        if (trimmedLine.includes('üè∑Ô∏è HASHTAGS')) {
          const hashtagMatch = trimmedLine.match(/üè∑Ô∏è.*?\[(.*?)\]/);
          post.hashtags = hashtagMatch ? hashtagMatch[1] : '#inmobiliaria #bienesonraices #cdmx';
        }

        if (trimmedLine.includes('üì∏ VISUAL')) {
          const visualMatch = trimmedLine.match(/üì∏.*?\[(.*?)\]/);
          post.visualDescription = visualMatch ? visualMatch[1] : 'Imagen de propiedad premium';
        }

        if (trimmedLine.includes('‚è∞ HORARIO √ìPTIMO:')) {
          const timeMatch = trimmedLine.match(/‚è∞.*?\[(.*?)\]/);
          post.optimalTime = timeMatch ? timeMatch[1] : '10:00 AM';
        }

        if (trimmedLine.includes('üìä KPI OBJETIVO:')) {
          const kpiMatch = trimmedLine.match(/üìä.*?\[(.*?)\]/);
          post.kpiTarget = kpiMatch ? kpiMatch[1] : 'Engagement rate';
        }

        if (trimmedLine.includes('üí∞ OBJETIVO DE NEGOCIO:')) {
          const businessMatch = trimmedLine.match(/üí∞.*?\[(.*?)\]/);
          post.businessObjective = businessMatch ? businessMatch[1] : 'Brand awareness';
        }
      });

      // Extraer el contenido principal (todo lo que est√° entre COPY PRINCIPAL y CTA)
      const contentStartIndex = section.indexOf('üìù COPY PRINCIPAL:');
      const contentEndIndex = section.indexOf('üî• CTA ESPEC√çFICO:');

      if (contentStartIndex !== -1 && contentEndIndex !== -1) {
        const mainContent = section.substring(contentStartIndex, contentEndIndex)
          .replace('üìù COPY PRINCIPAL:', '')
          .trim();
        post.mainContent = mainContent;
      } else {
        post.mainContent = 'Contenido generado para redes sociales inmobiliarias.';
      }

      // Generar horario de publicaci√≥n espec√≠fico
      const dayMap = {
        'Lunes': 1, 'Martes': 2, 'Mi√©rcoles': 3, 'Jueves': 4,
        'Viernes': 5, 'S√°bado': 6, 'Domingo': 0
      };

      const publishDate = new Date();
      const currentDay = publishDate.getDay();
      const targetDay = dayMap[post.publishDay] || 1;
      const daysToAdd = (targetDay - currentDay + 7) % 7;
      publishDate.setDate(publishDate.getDate() + daysToAdd);

      post.scheduledDateTime = publishDate.toISOString();
      post.formattedDate = publishDate.toLocaleDateString('es-ES', { 
        weekday: 'long', 
        year: 'numeric', 
        month: 'long', 
        day: 'numeric' 
      });

      // Asignar horarios √≥ptimos por plataforma
      const platformOptimalTimes = {
        'Instagram': '10:00 AM',
        'Facebook': '2:00 PM', 
        'LinkedIn': '8:00 AM'
      };

      if (!post.optimalTime || post.optimalTime === 'Horario √≥ptimo') {
        post.optimalTime = platformOptimalTimes[post.platform] || '10:00 AM';
      }

      // Validar que el post tenga los campos m√≠nimos requeridos
      if (post.platform && (post.title || post.mainContent)) {
        posts.push(post);
        postCounter++;
      }

    } catch (error) {
      console.log(`Error procesando secci√≥n ${index}:`, error.message);
    }
  });

  return posts;
}

// Procesar el contenido
const structuredPosts = parseAIContentToStructured(aiResponse, contextData);

// Crear resumen ejecutivo
const executiveSummary = {
  totalPosts: structuredPosts.length,
  weekNumber: contextData.semana_numero,
  generationDate: contextData.fecha_actual,
  month: contextData.mes_actual,
  year: contextData.a√±o_actual,
  platformDistribution: {
    Instagram: structuredPosts.filter(p => p.platform === 'Instagram').length,
    Facebook: structuredPosts.filter(p => p.platform === 'Facebook').length,
    LinkedIn: structuredPosts.filter(p => p.platform === 'LinkedIn').length
  },
  estimatedReach: structuredPosts.length * 500, // Estimaci√≥n conservadora
  expectedEngagement: structuredPosts.length * 50,
  potentialLeads: Math.ceil(structuredPosts.length * 1.5)
};

// Preparar salida final
const finalOutput = {
  success: true,
  summary: executiveSummary,
  contentCalendar: structuredPosts,
  rawAIResponse: aiResponse.substring(0, 1000) + '...', // Primeros 1000 caracteres
  generatedAt: new Date().toISOString(),
  nextGenerationDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]
};

// Retornar resultado estructurado
return [finalOutput];
