={
  "model": "sora-2-image-to-video",
  "input": {
    "prompt": "{{ $('Check: Has Image?').item.json['Video Description (Prompt)'] }}",
    "image_urls": [
      "{{ $json.data.url }}"
    ],
    "aspect_ratio": "{{ $('Check: Has Image?').item.json['Aspect Ratio'] }}",
    "quality": "{{ $('Check: Has Image?').item.json['Video Quality'] }}"
  }
}

{
  "model": "sora-2-text-to-video",
  "input": {
    "prompt": "Realistic police bodycam footage, first-person perspective, with a timestamp overlay. An officer approaches a silver sedan on a quiet suburban street. The officer looks into the open driver's window to find a tabby cat sitting upright in the driver's seat, wearing a red collar and holding the steering wheel. The car then screeches its tires and speeds off. The camera becomes very shaky as the officer starts running after the car.",
    "aspect_ratio": "portrait",
    "quality": "hd",
    "remove_watermark": true
  }
}

