return items.map(item => {
  const originalText = $input.first().json.message.content

  if (typeof originalText !== 'string') {
    return {
      json: {
        error: 'El campo "text" no existe o no es un texto',
        original: originalText
      }
    };
  }

  let cleaned = originalText
    .replace(/<[^>]*>/g, '')                 // Quitar HTML
    .replace(/\r?\n|\r/g, ' ')               // Saltos de l√≠nea a espacio
    .replace(/\t+/g, ' ')                    // Tabs a espacio
    .replace(/[^\p{L}\p{N},¬ø?\s]+/gu, '')    // Letras, n√∫meros, comas, espacios, ¬ø, ?
    .replace(/\s+/g, ' ')                    // Normalizar m√∫ltiples espacios
    .trim();                                 // Quitar espacios extremos

  return {
    json: {
      cleaned
    }
  };
});

